import React, { Component } from "react";
import ReactDOM from "react-dom";
import { default as ReactSelect, components } from "react-select";
import { parse } from "papaparse"; // CSV parsing library
import "./ColumnFilter.css";

const Option = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};

const customStyles = {
  control: (provided) => ({
    ...provided,
    backgroundColor: "white", // Change the background color
    color: "black", // Change the text color
  }),
  option: (provided, state) => ({
    ...provided,
    backgroundColor: state.isFocused ? "#e6f7ff" : "transparent", // Change the hover color
    color: "black", // Change the text color
  }),
};

export default class Example extends Component {
  constructor(props) {
    super(props);
    this.state = {
      optionSelected: null,
      columnNames: [],
    };
  }

  componentDidMount() {
    this.loadCSVData();
  }

  loadCSVData() {
    // Load the CSV file from a relative path
    const csvFilePath = require("./data.csv");

    fetch(csvFilePath)
      .then((response) => response.text())
      .then((csvText) => {
        const { data } = parse(csvText, { header: true });
        const columnNames = Object.keys(data[0]);
        this.setState({ columnNames });
      })
      .catch((error) => {
        console.error("Error loading CSV file:", error);
      });
  }

  handleChange = (selected) => {
    this.setState({
      optionSelected: selected,
    });
  };

  render() {
    const { columnNames, optionSelected } = this.state;

    if (columnNames.length === 0) {
      return <p>Loading...</p>;
    }

    const options = [
      { value: "select-all", label: "Select All" },
      ...columnNames.map((name) => ({ value: name, label: name })),
    ];

    return (
      <div>
        <ReactSelect
          styles={customStyles}
          options={options}
          isMulti
          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option,
          }}
          onChange={this.handleChange}
          allowSelectAll={true}
          value={optionSelected}
          placeholder="Custom Placeholder"
        />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Example />, rootElement);
