import React, { useState } from 'react';
import './ImportCSV.css';

const ImportCSV = () => {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [isDragging1, setIsDragging1] = useState(false);
  const [isDragging2, setIsDragging2] = useState(false);

  const handleDragOver1 = (e) => {
    e.preventDefault();
    setIsDragging1(true);
  };

  const handleDragLeave1 = () => {
    setIsDragging1(false);
  };

  const handleDrop1 = (e) => {
    e.preventDefault();
    setIsDragging1(false);
    const droppedFile = e.dataTransfer.files[0];
    setFile1(droppedFile);
  };

  const handleDragOver2 = (e) => {
    e.preventDefault();
    setIsDragging2(true);
  };

  const handleDragLeave2 = () => {
    setIsDragging2(false);
  };

  const handleDrop2 = (e) => {
    e.preventDefault();
    setIsDragging2(false);
    const droppedFile = e.dataTransfer.files[0];
    setFile2(droppedFile);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (file1 && file2) {
      const formData = new FormData();
      formData.append('file1', file1);
      formData.append('file2', file2);

      try {
        const response = await fetch('/api/save-csv-files', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          // Files saved successfully
          console.log('CSV files saved');
        } else {
          // Handle the error
          console.error('Failed to save CSV files');
        }
      } catch (error) {
        // Handle network or server errors
        console.error('Network or server error:', error);
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <div className="dropzone-container">
          <div
            className={`dropzone ${isDragging1 ? 'dragging' : ''}`}
            onDragOver={handleDragOver1}
            onDragLeave={handleDragLeave1}
            onDrop={handleDrop1}
          >
            {isDragging1 ? (
              <div className="drag-icon">ðŸ”½</div>
            ) : (
              <div className="drag-icon" style={{ color: 'yellow' }}>
                ðŸ“‚
              </div>
            )}
            <div className="dropzone-text">
              {file1 ? file1.name : 'Drag Train Data'}
            </div>
          </div>
          <div
            className={`dropzone ${isDragging2 ? 'dragging' : ''}`}
            onDragOver={handleDragOver2}
            onDragLeave={handleDragLeave2}
            onDrop={handleDrop2}
          >
            {isDragging2 ? (
              <div className="drag-icon">ðŸ”½</div>
            ) : (
              <div className="drag-icon">ðŸ“‚</div>
            )}
            <div className="dropzone-text">
              {file2 ? file2.name : 'Drag Test Data'}
            </div>
          </div>
        </div>
        <button type="submit">Import</button>
      </form>
    </div>
  );
};

export default ImportCSV;
