import React, { Component } from "react";
import ReactDOM from "react-dom";
import { default as ReactSelect } from "react-select";
import { components } from "react-select";
import { parse } from "papaparse"; // CSV parsing library

const Option = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};

export default class Example extends Component {
  constructor(props) {
    super(props);
    this.state = {
      optionSelected: null,
      columnNames: [],
      csvData: [],
    };
  }

  componentDidMount() {
    // Load the CSV data from an existing array
    const csvArray = [
      ["Name", "Age", "Country"],
      ["John Doe", "25", "USA"],
      ["Jane Smith", "30", "Canada"],
      ["Michael Johnson", "35", "Australia"],
    ];

    this.parseCSVData(csvArray);
  }

  parseCSVData = (csvArray) => {
    const columnNames = csvArray[0];
    const csvData = csvArray.slice(1);

    console.log("Column Names:", columnNames);
    console.log("CSV Data:", csvData);

    this.setState({ columnNames, csvData });
  };

  handleChange = (selected) => {
    this.setState({
      optionSelected: selected,
    });
  };

  render() {
    const { columnNames, optionSelected, csvData } = this.state;

    return (
      <div>
        {columnNames.length > 0 && (
          <ReactSelect
            options={columnNames.map((name) => ({ value: name, label: name }))}
            isMulti
            closeMenuOnSelect={false}
            hideSelectedOptions={false}
            components={{
              Option,
            }}
            onChange={this.handleChange}
            allowSelectAll={true}
            value={optionSelected}
          />
        )}

        {csvData.length > 0 && (
          <table>
            <thead>
              <tr>
                {columnNames.map((name) => (
                  <th key={name}>{name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {csvData.map((row, index) => (
                <tr key={index}>
                  {row.map((value, colIndex) => (
                    <td key={colIndex}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Example />, rootElement);
