import React, { Component } from "react";
import ReactDOM from "react-dom";
import { default as ReactSelect, components } from "react-select";
import { parse } from "papaparse"; // CSV parsing library
import "./ColumnFilter.css";

const Option = (props) => {
  if (props.value === "select-all") {
    return (
      <components.Option {...props}>
        <div style={{ display: "flex", alignItems: "center" }}>
          <input
            type="checkbox"
            checked={props.isSelected}
            onChange={() => props.selectProps.selectOption(props.data)}
          />
          <label style={{ marginLeft: "8px", textAlign: "left", flex: "1" }}>
            {props.label}
          </label>
        </div>
      </components.Option>
    );
  }

  return (
    <components.Option {...props}>
      <div style={{ display: "flex", alignItems: "center" }}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => props.selectProps.selectOption(props.data)}
        />
        <label style={{ marginLeft: "8px", textAlign: "left", flex: "1" }}>
          {props.label}
        </label>
      </div>
    </components.Option>
  );
};

const customStyles = {
  control: (provided) => ({
    ...provided,
    backgroundColor: "white", // Change the background color
    color: "black", // Change the text color
  }),
  option: (provided, state) => ({
    ...provided,
    backgroundColor: state.isFocused ? "#e6f7ff" : "transparent", // Change the hover color
    color: "black", // Change the text color
  }),
};

export default class Example extends Component {
  // ... rest of the code ...

  render() {
    const { columnNames, optionSelected, csvData } = this.state;
    const topRows = csvData.slice(0, 3); // Display only the top 3 rows

    const options = [
      { value: "select-all", label: "Select All" },
      ...columnNames.map((name) => ({ value: name, label: name })),
    ];

    return (
      <div>
        {columnNames.length > 0 && (
          <ReactSelect
            styles={customStyles}
            options={options}
            isMulti
            closeMenuOnSelect={false}
            hideSelectedOptions={false}
            components={{
              Option,
            }}
            onChange={this.handleChange}
            allowSelectAll={true}
            value={optionSelected}
            placeholder="Custom Placeholder" // Change the placeholder text here
          />
        )}

        {/* ... rest of the code ... */}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Example />, rootElement);
