import React, { useEffect, useState } from 'react';
import axios from 'axios';
import dc from 'dc';
import crossfilter from 'crossfilter2';
import * as d3 from 'd3';

const CrossFilterChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('/api/data');
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    if (data && data.length > 0) {
      createChart();
    }
  }, [data]);

  const createChart = () => {
    const ndx = crossfilter(data);
    const dimension = ndx.dimension((d) => d[0]); // Assuming the first column is used for dimension
    const group = dimension.group().reduceCount();

    const chart = dc.rowChart('#chart-container');

    chart
      .width(600)
      .height(400)
      .dimension(dimension)
      .group(group)
      .elasticX(true)
      .render();

    chart.on('filtered', () => {
      const selectedData = dimension.top(Infinity);
      console.log(selectedData); // Display selected data in the console
    });

    dc.renderAll();
  };

  return <div id="chart-container"></div>;
};

export default CrossFilterChart;
