import Papa from 'papaparse';
import { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';

function Home() {
  const [chartData, setChartData] = useState(null);
  const [showDoubleBarGraph, setShowDoubleBarGraph] = useState(true);

  useEffect(() => {
    Papa.parse('/static/data.csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      delimiter: "",
      complete: (result) => {
        const labelCounts = {};

        result.data.forEach((item) => {
          const label = item['req1Product'];

          if (label) {
            if (labelCounts[label]) {
              labelCounts[label] += 1;
            } else {
              labelCounts[label] = 1;
            }
          }
        });

        const labels = Object.keys(labelCounts);
        const datasets = labels.map((label) => ({
          label: label,
          data: [labelCounts[label]],
          backgroundColor: "#defde0"
        }));

        const initialChartData = {
          labels: labels,
          datasets: datasets
        };

        setChartData(initialChartData);
      }
    });
  }, []);

  const handleColumnClick = async (event, activeElements) => {
    if (activeElements.length > 0) {
      const labelIndex = activeElements[0].index;
      const clickedLabel = chartData.labels[labelIndex];

      if (showDoubleBarGraph) {
        const predictionLabelCounts = {};
        await new Promise((resolve, reject) => {
          Papa.parse('/static/data.csv', {
            download: true,
            header: true,
            dynamicTyping: true,
            delimiter: "",
            complete: (result) => {
              result.data.forEach((item) => {
                const prediction = item['prediction'];
                const label = item['req1Product'];

                if (label === clickedLabel && prediction !== undefined) {
                  if (predictionLabelCounts[prediction]) {
                    predictionLabelCounts[prediction] += 1;
                  } else {
                    predictionLabelCounts[prediction] = 1;
                  }
                }
              });

              const labelCountChartData = {
                labels: Object.keys(predictionLabelCounts),
                datasets: [
                  {
                    label: "Count",
                    data: Object.values(predictionLabelCounts),
                    backgroundColor: ["#ff4040", "#B6E4EB"]
                  }
                ]
              };

              setChartData(labelCountChartData);
              setShowDoubleBarGraph(false);

              resolve();
            },
            error: (error) => {
              reject(error);
            }
          });
        });
      } else {
        const labelCounts = {};
        await new Promise((resolve, reject) => {
          Papa.parse('/static/data.csv', {
            download: true,
            header: true,
            dynamicTyping: true,
            delimiter: "",
            complete: (result) => {
              result.data.forEach((item) => {
                const label = item['req1Product'];

                if (label) {
                  if (labelCounts[label]) {
                    labelCounts[label] += 1;
                  } else {
                    labelCounts[label] = 1;
                  }
                }
              });

              const labels = Object.keys(labelCounts);
              const datasets = labels.map((label) => ({
                label: label,
                data: [labelCounts[label]],
                backgroundColor: "#defde0"
              }));

              const labelCountChartData = {
                labels: labels,
                datasets: datasets
              };

              setChartData(labelCountChartData);
              setShowDoubleBarGraph(true);

              resolve();
            },
            error: (error) => {
              reject(error);
            }
          });
        });
      }
    }
  };

  return (
    <div>
      <h4>Analysis</h4>
      <div className="import-boxes">
        {/* Import boxes code here */}
      </div>
      {chartData ? (
        <div className="graph-container">
          <div>
            <Bar options={{ onClick: handleColumnClick }} data={chartData} />
            <button onClick={handleCountButtonClick}>
              {showDoubleBarGraph ? "Hide Double Bar" : "Show Double Bar"}
            </button>
          </div>
        </div>
      ) : (
        <div>Loading...</div>
      )}
      <style>{`
        .graph-container {
          width: 600px; /* Set the desired width */
          height: 400px; /* Set the desired height */
          margin: 0 auto; /* Center the graph container horizontally */
        }
      `}</style>
    </div>
  );
}

export default Home;
