import React, { Component } from "react";
import ReactDOM from "react-dom";
import { default as ReactSelect, components } from "react-select";
import { parse } from "papaparse"; // CSV parsing library
import "./ColumnFilter.css";

const Option = (props) => {
  // ... unchanged Option component code ...
};

const customStyles = {
  control: (provided) => ({
    ...provided,
    backgroundColor: "white", // Change the background color
    color: "black", // Change the text color
  }),
  option: (provided, state) => ({
    ...provided,
    backgroundColor: state.isFocused ? "#e6f7ff" : "transparent", // Change the hover color
    color: "black", // Change the text color
  }),
};

export default class Example extends Component {
  constructor(props) {
    super(props);
    this.state = {
      optionSelected: null,
      columnNames: null, // Initialize columnNames as null
      csvData: [],
    };
  }

  componentDidMount() {
    // Load the CSV file from a relative path
    const csvFilePath = require("./data.csv");

    fetch(csvFilePath)
      .then((response) => response.text())
      .then((csvText) => {
        const { data } = parse(csvText, { header: true });
        const columnNames = Object.keys(data[0]);
        const csvData = data.map((row) => Object.values(row));
        this.setState({ columnNames, csvData });
      })
      .catch((error) => {
        console.error("Error loading CSV file:", error);
      });
  }

  handleChange = (selected) => {
    this.setState({
      optionSelected: selected,
    });
  };

  render() {
    const { columnNames, optionSelected, csvData } = this.state;
    const topRows = csvData.slice(0, 3); // Display only the top 3 rows

    if (columnNames === null) {
      return <div>Loading...</div>;
    }

    const options = [
      { value: "select-all", label: "Select All" },
      ...columnNames.map((name) => ({ value: name, label: name })),
    ];

    return (
      <div>
        <ReactSelect
          styles={customStyles}
          options={options}
          isMulti
          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option,
          }}
          onChange={this.handleChange}
          allowSelectAll={true}
          value={optionSelected}
          placeholder="Custom Placeholder"
        />

        {topRows.length > 0 && (
          <table className="csv-table">
            <thead>
              <tr>
                {columnNames.map((name) => (
                  <th key={name}>{name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {topRows.map((row, index) => (
                <tr key={index}>
                  {row.map((value, colIndex) => (
                    <td key={colIndex}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Example />, rootElement);
