import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { useDropzone } from 'react-dropzone';

const App = () => {
  const [csvData, setCsvData] = useState(null);
  const [chartData, setChartData] = useState(null);

  const handleDrop = async (acceptedFiles) => {
    const file = acceptedFiles[0];
    const formData = new FormData();
    formData.append('file', file);
    try {
      const response = await axios.post('http://localhost:5000/api/process_csv', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setCsvData(response.data.csvData);
      setChartData(response.data.chartData);
    } catch (error) {
      console.error(error);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop: handleDrop });

  return (
    <div className="App">
      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
        <input {...getInputProps()} />
        {isDragActive ? <p>Drop the CSV file here...</p> : <p>Drag 'n' drop a CSV file here, or click to select file</p>}
      </div>
      {csvData && (
        <div>
          <h2>CSV Data:</h2>
          <pre>{csvData}</pre>
        </div>
      )}
      {chartData && (
        <div>
          <h2>Chart:</h2>
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
};

export default App;
