import React, { Component } from "react";
import ReactDOM from "react-dom";
import { default as ReactSelect } from "react-select";
import { components } from "react-select";
import { parse } from "papaparse"; // CSV parsing library

const Option = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};

export default class Example extends Component {
  constructor(props) {
    super(props);
    this.state = {
      optionSelected: null,
      columnNames: [],
      csvData: [],
      loadingError: false,
    };
  }

  componentDidMount() {
    // Load the CSV file on component mount
    this.loadCSV();
  }

  loadCSV = () => {
    const filePath = "data.csv";

    fetch(filePath)
      .then((response) => response.text())
      .then((csvText) => {
        const results = parse(csvText, {
          header: true,
        });

        const columnNames = results.data[0];
        const csvData = results.data.slice(1);

        console.log("Column Names:", columnNames);
        console.log("CSV Data:", csvData);

        this.setState({ columnNames, csvData });
      })
      .catch((error) => {
        console.error(error);
        this.setState({ loadingError: true });
      });
  };

  handleChange = (selected) => {
    this.setState({
      optionSelected: selected,
    });
  };

  render() {
    const { columnNames, optionSelected, csvData, loadingError } = this.state;

    if (loadingError) {
      return <div>Error occurred while loading the CSV file.</div>;
    }

    return (
      <div>
        {columnNames.length > 0 && (
          <ReactSelect
            options={columnNames.map((name) => ({ value: name, label: name }))}
            isMulti
            closeMenuOnSelect={false}
            hideSelectedOptions={false}
            components={{
              Option,
            }}
            onChange={this.handleChange}
            allowSelectAll={true}
            value={optionSelected}
          />
        )}

        {csvData.length > 0 && (
          <table>
            <thead>
              <tr>
                {columnNames.map((name) => (
                  <th key={name}>{name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {csvData.map((row, index) => (
                <tr key={index}>
                  {row.map((value, colIndex) => (
                    <td key={colIndex}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Example />, rootElement);
