// import OscarData from '../../../static/dta.csv';
import Papa from 'papaparse';
import { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Home() {
  const [chartData, setChartData] = useState({
    datasets: []
  });
  const [chartOptions, setChartOptions] = useState({});
  const [labelCountData, setLabelCountData] = useState([]);
  const [clickedLabelIndex, setClickedLabelIndex] = useState(null); // New state variable to store clicked label index

  useEffect(() => {
    Papa.parse('/static/data.csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      delimiter: "",
      complete: ((result) => {
        const labelCounts = {}; // Object to store label counts

        result.data.forEach((item) => {
          const label = item['req1Product'];

          if (label) {
            if (labelCounts[label]) {
              labelCounts[label] += 1;
            } else {
              labelCounts[label] = 1;
            }
          }
        });

        setChartData({
          labels: Object.keys(labelCounts),
          datasets: [
            {
              label: "Count",
              data: Object.values(labelCounts),
              borderColor: "white",
              backgroundColor: "#ffb347"
            }
          ]
        });

        setChartOptions({
          responsive: true,
          plugins: {
            legend: {
              position: 'top'
            },
            title: {
              display: true,
              text: "Count of req1Product Values"
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: "Count"
              }
            }
          }
        });
      })
    });
  }, []);

  const handleColumnClick = async (event, activeElements) => {
    if (activeElements.length > 0) {
      const labelIndex = activeElements[0].index;
      setClickedLabelIndex(labelIndex); // Update clicked label index

      const clickedLabel = chartData.labels[labelIndex];

      // Create a new chart for the label count from the 'prediction' column
      const predictionLabelCounts = {};
      await new Promise((resolve, reject) => {
        Papa.parse('/static/data.csv', {
          download: true,
          header: true,
          dynamicTyping: true,
          delimiter: "",
          complete: ((result) => {
            result.data.forEach((item) => {
              const prediction = item['prediction'];
              const label = item['req1Product'];

              if (label === clickedLabel && prediction !== undefined) {
                if (predictionLabelCounts[prediction]) {
                  predictionLabelCounts[prediction] += 1;
                } else {
                  predictionLabelCounts[prediction] = 1;
                }
              }
            });

            const labelCountChartData = {
              labels: Object.keys(predictionLabelCounts),
              datasets: [
                {
                  label: "Count",
                  data: Object.values(predictionLabelCounts),
                  borderColor: "white",
                  backgroundColor: ["#ff4040", "#B6E4EB"] 
                }
              ]
            };

            const labelCountChartOptions = {
              responsive: true,
              plugins: {
                legend: {
                  position: 'top'
                },
                title: {
                  display: true,
                  text: `Label Count for ${clickedLabel}`
                }
              },
              scales: {
                y: {
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: "Count"
                  }
                }
              }
            };

            setLabelCountData((prevData) => {
              const newData = [...prevData];
              newData[labelIndex] = {
                chartData: labelCountChartData,
                chartOptions: labelCountChartOptions
              };
              return newData;
            });

            resolve();
          }),
          error: ((error) => {
            reject(error);
          })
        });
      });
    }
  };

  return (
    <div>
      <h4>Analysis</h4>
      <div className="import-boxes">
        {/* Import boxes code here */}
      </div>
      {chartData ? (
        <div className="graph-container">
          <div>
            <Bar options={{ onClick: handleColumnClick }} data={chartData} />
          </div>
          <div>
            {labelCountData.length > 0 && labelCountData[clickedLabelIndex] && (
              <div>
                <h4>Label Count for {chartData.labels[clickedLabelIndex]}</h4>
                <Bar
                  options={labelCountData[clickedLabelIndex].chartOptions}
                  data={labelCountData[clickedLabelIndex].chartData}
                />
              </div>
            )}
          </div>
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );


}

export default Home;
