import OscarData from './data.csv';
import Papa from 'papaparse';
import { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Home() {
  const [chartData, setChartData] = useState({
    datasets: []
  });
  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    Papa.parse(OscarData, {
      download: true,
      header: true,
      dynamicTyping: true,
      delimiter: "",
      complete: ((result) => {
        console.log(result);
        const labelCounts = {}; // Object to store label counts

        result.data.forEach((item) => {
          const label = item[' "req1Product"'];

          if (label && typeof label === 'string') {
            // If label is not empty and of string type
            if (labelCounts[label]) {
              labelCounts[label] += 1; // Increment count if label already exists
            } else {
              labelCounts[label] = 1; // Initialize count if label does not exist
            }
          }
        });

        setChartData({
          labels: Object.keys(labelCounts), // Use label values as chart labels
          datasets: [
            {
              label: "OSCAR WINNER",
              data: Object.values(labelCounts), // Use label counts as chart data
              borderColor: "white",
              backgroundColor: "red"
            }
          ]
        });

        setChartOptions({
          responsive: true,
          plugins: {
            legend: {
              position: 'top'
            },
            title: {
              display: true,
              text: "ALL OSCAR WINNERS SINCE 1928"
            }
          }
        });
      })
    });
  }, []);

  return (
    <div>
      <h1>HOME PAGE</h1>
      {chartData.datasets.length > 0 ? (
        <div>
          <Bar options={chartOptions} data={chartData} />
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
}

export default Home;
